                    <a href="#">
                      <button className="btn btn-primary">Order Now</button>
                    </a>

                  </div>
                  <p>prices may on delected date.</p>

                </div>
              </div>
              </div>

            </div>
                </>


               )
             }
             )
           }


          

          </div>

        </div>

      </div>

    </div>
    </>
  )
}

export default GalleryReact
